const pool = new Pool({
    user: 'artdb_user',
    host: 'localhost',
    database: 'artdb-api',
    password: 'password',
    port: 5432
});

const getArt = (request, response) => {
    pool.query('SELECT * FROM artdb_api ORDER BY id ASC', (error, results) => {
        if (error) {
            throw error;
        }
        response.status(200).json(results.rows);
    });
};

